import React from 'react';
import './GalleryPage.css';

const GalleryPage: React.FC = () => {
  const screenshots = [
    { id: 'landing', title: 'Main Interface', image: '/images/WebworldMain.png', description: 'The central hub where all character interactions begin' },
    { id: 'character', title: 'Character Panel', image: '/images/CharacterPanelDetail.png', description: 'Deep dive into character psychology and state' },
    { id: 'core', title: 'Core State', image: '/images/CharacterExplorerCoreTab.png', description: 'The foundational drives and motivations' },
    { id: 'memory', title: 'Memory System', image: '/images/CharacterExplorerMemoryTab.png', description: 'How characters learn and remember' },
    { id: 'social', title: 'Social Dynamics', image: '/images/CharacterExplorerSocialTab.png', description: 'Relationship modeling and interaction patterns' },
    { id: 'signals', title: 'Drive Signals', image: '/images/CharacterExplorerSignalsTab.png', description: 'Real-time motivation and decision making' },
    { id: 'director', title: 'Director\'s Chair', image: '/images/DirectorsChairComposite.png', description: 'Direct control and character interaction interface' },
    { id: 'world', title: 'Dynamic World', image: '/images/Webworld_main_screen.png', description: 'The living environment characters inhabit' }
  ];

  return (
    <div className="gallery-page">
      <div className="gallery-header">
        <h1>ImprovAI Gallery</h1>
        <p>Explore the interface and capabilities in detail</p>
      </div>

      <div className="gallery-grid">
        {screenshots.map((item) => (
          <div key={item.id} className="gallery-item">
            <div className="gallery-image">
              <img src={item.image} alt={item.title} />
            </div>
            <div className="gallery-content">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GalleryPage;
